package com.nihon0tc.ad;import android.app.Activity;import com.google.ads.AdSize;import com.google.ads.mediation.MediationAdRequest;import com.google.ads.mediation.customevent.CustomEventBanner;import com.google.ads.mediation.customevent.CustomEventBannerListener;import com.nihon0tc.util.LogUtil;public abstract class CustomAd implements CustomEventBanner {		@Override	public void requestBannerAd(			final CustomEventBannerListener listener,            final Activity activity,            String label,            String serverParameter,            AdSize adSize,            MediationAdRequest request) {			instance  = this;						TAG = label;			KeyInfo = serverParameter;			LogUtil.trace(TAG, "setAd/" + label);					}	private static CustomAd instance = null;	public static CustomAd getInstance(){return instance;} 		protected static String TAG = "Ad";	public String getTAG(){return TAG;}	protected static String KeyInfo = null;	public String getKeyInfo(){return KeyInfo;}		public abstract void start();	public abstract void stop();}